import styles from './App.module.css';
import poweredImage from './assets/powered.png';
import leftArrowImage from './assets/leftarrow.png';
import {useState} from "react";
import {levels, calculateImc, Level} from "./helpers/imc";
import {GridItem} from "./components/GridItem";


const App = () => {

    const [heightField, setHeightField] = useState<number>(0);
    const [weightField, setWeightField] = useState<number>(0);
    const [toShow, setToShow] = useState<Level | null>(null)
    const handleCalculateButton = () => {
        if (heightField && weightField) {

            setToShow(calculateImc(heightField, weightField));

        } else {
            alert("Informe peso e/ou altura");
        }

        const handleBackButton = () =>{

        }
    }
    return (
        <div className={styles.main}>
            <header>
                <div className={styles.headerContainer}>
                    <img src={poweredImage}
                         alt="" width={150}/>
                </div>
            </header>
            <div className={styles.container}>
                <div className={styles.leftSide}>
                    <h1>Calcule o seu IMC.</h1>
                    <p>IMC é a sigla oara Índice de Massa Corporéa, parâmetro adotado pela Organização Mundial de Saúde
                        para calcular o peso ideal de cada pessoa.</p>
                    <input
                        type="number"
                        placeholder="Digite a sua altura. Ex: 1.5 (em metros)"
                        value={heightField > 0 ? heightField : ''}
                        onChange={e => setHeightField(parseFloat(e.target.value))}

                    />
                    <input
                        type="number"
                        placeholder="Digite o seu peso Ex: 75.3 (em KG)"
                        value={weightField > 0 ? weightField : ''}
                        onChange={e => setWeightField(parseFloat(e.target.value))}

                    />

                    <button onClick={handleCalculateButton}>
                        Calcular
                    </button>
                </div>
                <div className={styles.rightSide}>
                    {!toShow &&
                        <div className={styles.grid}>
                            {levels.map((item, key) => (
                                <GridItem key={key} item={item}/>
                            ))}
                        </div>
                    }
                    {toShow &&
                        <div className={styles.rightBig}>
                            <div className={styles.rightArrow} onClick={handleBackButton} >
                                <img src={leftArrowImage} alt={""} width={25} />
                            </div>
                            <GridItem item={toShow}/>
                        </div>
                    }
                </div>
            </div>
        </div>
    );

}
export default App;
